import React, { useEffect, useState } from 'react';
import 'react-dropdown/style.css';

const PizzaItems = () => {
    const [pizzaType] = useState([
        "Neapolitan",
        "NewYork",
        "Deep Dish",
    ]);
    const [defaultPizzaType, setPizzaType] = useState(pizzaType[0]); //because we like neo the most
    const yeastType = ["Biga", "Poolish", "SourDough"];
    const [servings, setServings] = useState(2);

    const changePizzaType = ({ target }) => {
        const { label, value } = target;
        setPizzaType(prevState => ({
            ...prevState,
            label: label,
            value: value,
        }
        ))
        console.log(value);
    }

    return (
        <div>
            <select>
                {pizzaType.map(item => (
                    <option onChange={e => changePizzaType(e)}
                        key={item}
                        value={item}
                    >
                        {item}
                    </option>
                ))}
            </select>
        </div>
    );

}

const PizzaDoughCalc = () => {
    useEffect(() => {
        document.title = "Tonight We're having pizza!"
    }, [])
    return (
        <div>
            <h1>Pizza Dough Calculator</h1>
            <div>{PizzaItems()}</div>
        </div>
    );
}

export default PizzaDoughCalc;
