import React, {useEffect, useState} from 'react';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';

const PizzaItems = () => {
    const pizzaType = ["neoplotan", "New York", "DeepDish"];
    const [defaultPizzaType, SetPizzaType]= useState(pizzaType[0]); //because we like neo the most
    const yeastType = ["Biga", "Poolish", "SourDough"];
    const [servings, setServings] = useState(2);

SetPizzaType = (e) => {
    defaultPizzaType = e.target.value;
}

    return (
        <div>
            <Dropdown options={pizzaType} onChange={SetPizzaType()} value={defaultPizzaType} placeholder="What Type of Pizza You making?" />;
        </div>
    );

}

const PizzaDoughCalc = () => {
    useEffect(() => {
        document.title = "Tonight We're having pizza!"
    }, [])
    return (
        <div>
            <h1>Pizza Dough Calculator</h1>
            <div>{PizzaItems()}</div>
        </div>
    );
}

export default PizzaDoughCalc;
